import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer } from "react-leaflet";
import { HeatmapLayer } from "react-leaflet-heatmap-layer";
import "leaflet/dist/leaflet.css";

const API_URL = "http://localhost:8080/api/data";

const HeatmapComponent = () => {
  const [heatmapData, setHeatmapData] = useState([]);

  useEffect(() => {
    // Fetch heatmap data from backend API
    fetch(`${API_URL}/heatmap`)
      .then((res) => res.json())
      .then((data) => {
        // Assuming data is in the format of an array of [x, y, intensity]
        setHeatmapData(data.map((item) => [item.posX, item.posY, item.intensity]));
      })
      .catch((error) => console.error("Error fetching heatmap data:", error));
  }, []);

  return (
    <div>
      <h3>Position Heatmap</h3>
      <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: "400px", width: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {heatmapData.length > 0 && (
          <HeatmapLayer
            fitBoundsOnLoad
            fitBoundsOnUpdate
            points={heatmapData}
            longitudeExtractor={(m) => m[1]} // X position
            latitudeExtractor={(m) => m[0]} // Y position
            intensityExtractor={(m) => m[2]} // Intensity
          />
        )}
      </MapContainer>
    </div>
  );
};

export default HeatmapComponent;
