import React, { useEffect } from "react";
import heatmap from "heatmap.js";

const HeatmapComponent = ({ data }) => {
  useEffect(() => {
    if (data.length > 0) {
      const heatmapInstance = heatmap.create({
        container: document.getElementById("heatmapContainer"),
      });

      // Map data into the format expected by the heatmap library
      const heatmapData = data.map((point) => ({
        x: point.posX,
        y: point.posY,
        value: point.intensity, // Intensity based on the metric or another property
      }));

      // Set the data for heatmap
      heatmapInstance.setData({
        max: Math.max(...heatmapData.map((d) => d.value)),
        data: heatmapData,
      });
    }
  }, [data]);

  return (
    <div className="heatmap">
      <h2>Positional Heatmap</h2>
      <div
        id="heatmapContainer"
        style={{ width: "100%", height: "400px", position: "relative" }}
      />
    </div>
  );
};

export default HeatmapComponent;
