import React, { useState } from "react";
import GraphComponent from "./components/GraphComponent";
import HeatmapComponent from "./components/HeatmapComponent";

const App = () => {
  const [selectedMetric, setSelectedMetric] = useState("humans");

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Tracking Dashboard</h1>

      {/* Dropdown to Select Graph Metric */}
      <label>Select Metric: </label>
      <select value={selectedMetric} onChange={(e) => setSelectedMetric(e.target.value)}>
        <option value="humans">Total Humans</option>
        <option value="posX">X Position</option>
        <option value="posY">Y Position</option>
        <option value="avgX">Avg X</option>
        <option value="avgY">Avg Y</option>
      </select>

      {/* Graph Component */}
      <GraphComponent selectedMetric={selectedMetric} />

      {/* Heatmap Component */}
      <HeatmapComponent />
    </div>
  );
};

export default App;
