import React, { useState, useEffect } from "react";
import axios from "axios";
import GraphComponent from "./GraphComponent";
import HeatmapComponent from "./HeatmapComponent";

function App() {
  const [graphData, setGraphData] = useState([]);
  const [heatmapData, setHeatmapData] = useState([]);
  const [selectedMetric, setSelectedMetric] = useState("numberOfHumans");
  const [startTime, setStartTime] = useState(Date.now() - 3600000); // Last 1 hour
  const [endTime, setEndTime] = useState(Date.now());

  // Fetch graph data based on selected metric
  useEffect(() => {
    axios
      .get("/api/data/graph")
      .then((response) => {
        setGraphData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching graph data:", error);
      });
  }, []);

  // Fetch heatmap data when the time range changes
  useEffect(() => {
    axios
      .get("/api/data/heatmap", {
        params: { startTime, endTime },
      })
      .then((response) => {
        setHeatmapData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching heatmap data:", error);
      });
  }, [startTime, endTime]);

  const handleMetricChange = (event) => {
    setSelectedMetric(event.target.value);
  };

  return (
    <div className="App">
      <h1>Tracking Data</h1>
      <div>
        <label>Select Metric: </label>
        <select value={selectedMetric} onChange={handleMetricChange}>
          <option value="numberOfHumans">Number of Humans</option>
          <option value="avgX">Average X Position</option>
          <option value="avgY">Average Y Position</option>
          <option value="totalHumans">Total Humans So Far</option>
        </select>
      </div>

      {/* Graph Component */}
      <GraphComponent
        data={graphData}
        selectedMetric={selectedMetric}
        startTime={startTime}
        endTime={endTime}
      />

      {/* Heatmap Component */}
      <HeatmapComponent data={heatmapData} />
    </div>
  );
}

export default App;
