import React, { useEffect, useState } from "react";
import heatmap from "heatmap.js"; // Import the heatmap.js library
import moment from "moment"; // Import moment.js for date formatting

const API_URL = "http://localhost:8080/api/data"; // API URL to fetch data

const HeatmapComponent = () => {
  const [heatmapData, setHeatmapData] = useState([]);
  const [startTime, setStartTime] = useState(null);
  const [endTime, setEndTime] = useState(null);

  // Fetch heatmap data on component mount
  useEffect(() => {
    fetch(`${API_URL}/heatmap`)
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched heatmap data:", data); // Log fetched data
        setHeatmapData(data);

        // Set startTime and endTime based on first and last timestamp
        if (data.length > 0) {
          setStartTime(data[0].timestamp); // First timestamp
          setEndTime(data[data.length - 1].timestamp); // Last timestamp
          console.log("Start time:", data[0].timestamp); // Log start time
          console.log("End time:", data[data.length - 1].timestamp); // Log end time
        }

        createHeatmap(data); // Create the heatmap after data is fetched
      })
      .catch((error) => {
        console.error("Error fetching heatmap data:", error);
      });
  }, []);

  // Function to format the timestamp to a human-readable format
  const formatTimestamp = (timestamp) => {
    const formatted = moment(timestamp).format("YYYY-MM-DD HH:mm:ss");
    console.log("Formatted timestamp:", formatted); // Log formatted timestamp
    return formatted;
  };

  // Function to create the heatmap
  const createHeatmap = (data) => {
    // Prepare the points from the data (converting them to the format heatmap.js expects)
    const points = data.map((point) => {
      const pointData = { x: point.posX, y: point.posY, value: point.intensity };
      console.log("Heatmap point:", pointData); // Log each point
      return pointData;
    });

    // Initialize the heatmap instance
    const heatmapInstance = heatmap.create({
      container: document.getElementById("heatmapContainer"), // Container for heatmap
    });

    // Set the heatmap data
    const maxIntensity = Math.max(...points.map((p) => p.value));
    console.log("Max intensity:", maxIntensity); // Log max intensity
    heatmapInstance.setData({
      max: maxIntensity, // Calculate max intensity
      data: points,
    });
  };

  return (
    <div>
      <h3>Positional Heatmap</h3>
      <div id="heatmapContainer" style={{ width: "100%", height: "500px" }}></div> {/* Heatmap container */}

      {/* Displaying the start and end time */}
      <div>
        <h4>Start Time: {startTime ? formatTimestamp(startTime) : "Loading..."}</h4>
        <h4>End Time: {endTime ? formatTimestamp(endTime) : "Loading..."}</h4>
      </div>
    </div>
  );
};

export default HeatmapComponent;
