import React, { useEffect, useState } from "react";
import { ScatterChart, Scatter, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer, ZAxis } from "recharts";
import moment from "moment"; // Import moment.js for date formatting

const API_URL = "http://localhost:8080/api/data"; // API endpoint

const HeatmapComponent = ({ setStartTime, setEndTime }) => {
  const [heatmapData, setHeatmapData] = useState([]);

  // Fetch heatmap data on component mount
  useEffect(() => {
    fetch(`${API_URL}/heatmap`)
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched heatmap data:", data); // Log data for debugging

        // Set start and end times based on first and last data points
        if (data.length > 0) {
          setStartTime(data[0].timestamp); // First timestamp
          setEndTime(data[data.length - 1].timestamp); // Last timestamp
        }

        // Format data for the heatmap
        const formattedData = data.map((point) => ({
          x: point.posX, // X position
          y: point.posY, // Y position
          z: point.intensity, // Intensity value (used for color scale)
          timestamp: point.timestamp, // Timestamp for tooltip
        }));

        setHeatmapData(formattedData);
      })
      .catch((error) => {
        console.error("Error fetching heatmap data:", error);
      });
  }, [setStartTime, setEndTime]); // Add dependencies to fix the warning

  // Format timestamp to a human-readable format using moment.js
  const formatTimestamp = (timestamp) => {
    return moment(timestamp).format("YYYY-MM-DD HH:mm:ss"); // Customize format if needed
  };

  // Custom Tooltip component to display heatmap data
  const CustomTooltip = ({ payload, label }) => {
    if (payload && payload.length > 0) {
      const data = payload[0].payload;
      return (
        <div className="custom-tooltip">
          <p>{`Time: ${formatTimestamp(data.timestamp)}`}</p>
          <p>{`X: ${data.x}, Y: ${data.y}`}</p>
          <p>{`Intensity: ${data.z}`}</p>
        </div>
      );
    }
    return null;
  };

  return (
    <div>
      <h3>Positional Heatmap</h3>

      {/* Heatmap Display */}
      <ResponsiveContainer width="100%" height={400}>
        <ScatterChart margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
          <XAxis
            type="number"
            dataKey="x"
            name="X Position"
            domain={[0, "auto"]} // Automatically scale x-axis
            ticks={[0, 5, 10, 15, 20, 25]} // Add more divisions on x-axis
          />
          <YAxis
            type="number"
            dataKey="y"
            name="Y Position"
            domain={[0, "auto"]} // Automatically scale y-axis
            ticks={[0, 5, 10, 15, 20, 25]} // Add more divisions on y-axis
          />
          <ZAxis
            type="number"
            dataKey="z"
            range={[0, 1000]} // Adjust range for intensity (z-axis)
          />
          <Tooltip content={<CustomTooltip />} />
          <Legend />
          <Scatter
            name="Heatmap"
            data={heatmapData}
            fill="#8884d8" // Default fill color
            shape="circle" // Shape of the points
          />
        </ScatterChart>
      </ResponsiveContainer>
    </div>
  );
};

export default HeatmapComponent;
