import React, { useEffect, useState } from "react";
import GraphComponent from "./GraphComponent";
import Heatmap from "./HeatmapComponent";
import "./App.css";

const API_URL = "http://localhost:8080/api/data";  // Change if backend URL is different

const App = () => {
  const [selectedMetric, setSelectedMetric] = useState("humans");
  const [heatmapData, setHeatmapData] = useState([]);

  // Fetch heatmap data
  useEffect(() => {
    fetch(`${API_URL}/heatmap`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setHeatmapData(data);
        } else {
          console.error("Unexpected API response:", data);
          setHeatmapData([]);
        }
      })
      .catch((error) => console.error("Error fetching heatmap data:", error));
  }, []);

  return (
    <div className="container">
      <h2>Tracking Graph</h2>
      <select value={selectedMetric} onChange={(e) => setSelectedMetric(e.target.value)}>
        <option value="humans">Total Humans</option>
        <option value="avgX">Average X Position</option>
        <option value="avgY">Average Y Position</option>
      </select>

      {/* Graph Component */}
      <GraphComponent selectedMetric={selectedMetric} />

      <h2>Heatmap</h2>
      <Heatmap data={heatmapData} />
    </div>
  );
};

export default App;
