import React, { useEffect, useState } from "react";
import Heatmap from "heatmap.js";

const API_URL = "http://localhost:8080/api/data";

const HeatmapComponent = ({ startTime, endTime }) => {
  const [heatmapData, setHeatmapData] = useState([]);

  useEffect(() => {
    if (startTime && endTime) {
      fetch(`${API_URL}/heatmap?startTime=${startTime}&endTime=${endTime}`)
        .then((res) => res.json())
        .then((data) => {
          setHeatmapData(data);
          updateHeatmap(data);
        });
    }
  }, [startTime, endTime]);

  const updateHeatmap = (data) => {
    const heatmapInstance = Heatmap.create({
      container: document.getElementById("heatmapContainer"),
      radius: 20,
    });

    const formattedData = data.map((d) => ({ x: d.posX, y: d.posY, value: d.intensity }));
    heatmapInstance.setData({ max: 100, data: formattedData });
  };

  return (
    <div>
      <h3>Heatmap</h3>
      <div id="heatmapContainer" style={{ width: "100%", height: "400px" }}></div>
    </div>
  );
};

export default HeatmapComponent;
