import React, { useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";
import Heatmap from "heatmap.js";

const API_URL = "http://localhost:8080/api";

const Dashboard = () => {
  const [graphData, setGraphData] = useState([]);
  const [heatmapData, setHeatmapData] = useState([]);
  const [selectedMetric, setSelectedMetric] = useState("human_count");

  useEffect(() => {
    fetch(`${API_URL}/graph-data?metric=${selectedMetric}`)
      .then((res) => res.json())
      .then((data) => setGraphData(data));
  }, [selectedMetric]);

  useEffect(() => {
    fetch(`${API_URL}/heatmap-data`)
      .then((res) => res.json())
      .then((data) => {
        setHeatmapData(data);
        updateHeatmap(data);
      });
  }, [graphData]);

  const updateHeatmap = (data) => {
    const heatmapInstance = Heatmap.create({
      container: document.getElementById("heatmapContainer"),
      radius: 20,
    });

    const formattedData = data.map((d) => ({ x: d.pos_x, y: d.pos_y, value: d.intensity }));
    heatmapInstance.setData({ max: 100, data: formattedData });
  };

  return (
    <div>
      {/* Dropdown Menu */}
      <select onChange={(e) => setSelectedMetric(e.target.value)} value={selectedMetric}>
        <option value="human_count">Number of Humans</option>
        <option value="pos_x">X Position of Human</option>
      </select>

      {/* Graph Component */}
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={graphData}>
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Line type="monotone" dataKey={selectedMetric} stroke="#8884d8" />
        </LineChart>
      </ResponsiveContainer>

      {/* Heatmap Component */}
      <div id="heatmapContainer" style={{ width: "100%", height: "400px" }}></div>
    </div>
  );
};

export default Dashboard;
