import React, { useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from "recharts";

const API_URL = "http://localhost:8080/api/data";

const GraphComponent = ({ setStartTime, setEndTime }) => {
  const [graphData, setGraphData] = useState([]);

  useEffect(() => {
    fetch(`${API_URL}/graph`)
      .then((res) => res.json())
      .then((data) => {
        setGraphData(data);
        if (data.length > 0) {
          setStartTime(data[0].timestamp);
          setEndTime(data[data.length - 1].timestamp);
        }
      });
  }, []);

  return (
    <div>
      <h3>Tracking Graph</h3>
      <ResponsiveContainer width="100%" height={400}>
        <LineChart data={graphData}>
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="cumulativeUsers" stroke="#ff7300" name="Total Humans" />
          <Line type="monotone" dataKey="avgX" stroke="#387908" name="Avg X Position" />
          <Line type="monotone" dataKey="avgY" stroke="#8884d8" name="Avg Y Position" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default GraphComponent;
